import React from "react";
import Message from "../preview/Message";

const Alert = (props)=>(
    <div className="alert-container">
    <ul>
        {props.validationMessages.map((message, index) => <li key={index}>{message}</li>)}
    </ul>
</div>
)
  

export default Alert
